import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import PasswordAnalyzer from './components/PasswordAnalyzer';
import ApiService from './services/api';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(145deg, #667eea 0%, #764ba2 50%, #f093fb 100%);
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
      radial-gradient(circle at 20% 80%, rgba(120, 119, 198, 0.3) 0%, transparent 50%),
      radial-gradient(circle at 80% 20%, rgba(255, 119, 198, 0.3) 0%, transparent 50%),
      radial-gradient(circle at 40% 40%, rgba(120, 219, 255, 0.2) 0%, transparent 50%);
  }
`;

const Header = styled.header`
  position: relative;
  z-index: 2;
  background: rgba(255, 255, 255, 0.08);
  backdrop-filter: blur(30px);
  border-bottom: 1px solid rgba(255, 255, 255, 0.12);
  padding: 48px 0;
  text-align: center;
  color: white;
`;

const Title = styled.h1`
  font-size: 52px;
  font-weight: 900;
  margin: 0 0 16px 0;
  background: linear-gradient(135deg, #ffffff 0%, #f0f9ff 30%, #e0f2fe 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  letter-spacing: -1px;
  text-shadow: 0 0 40px rgba(255, 255, 255, 0.3);
`;

const Subtitle = styled.p`
  font-size: 20px;
  margin: 0;
  opacity: 0.95;
  font-weight: 400;
  letter-spacing: 0.5px;
`;

const ContentContainer = styled.div`
  position: relative;
  z-index: 2;
  max-width: 900px;
  margin: 0 auto;
  padding: 60px 24px;
`;



const StatusIndicator = styled.div`
  position: fixed;
  top: 32px;
  right: 32px;
  padding: 12px 20px;
  border-radius: 50px;
  font-size: 14px;
  font-weight: 700;
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  z-index: 1000;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.12);
  transition: all 0.3s ease;

  ${props => props.connected ? `
    background: linear-gradient(135deg, rgba(34, 197, 94, 0.9) 0%, rgba(22, 163, 74, 0.9) 100%);
    color: white;
    box-shadow: 0 8px 32px rgba(34, 197, 94, 0.3);
  ` : `
    background: linear-gradient(135deg, rgba(239, 68, 68, 0.9) 0%, rgba(220, 38, 38, 0.9) 100%);
    color: white;
    box-shadow: 0 8px 32px rgba(239, 68, 68, 0.3);
  `}

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 12px 40px ${props => props.connected ? 'rgba(34, 197, 94, 0.4)' : 'rgba(239, 68, 68, 0.4)'};
  }
`;



function App() {
  const [serverConnected, setServerConnected] = useState(false);
  const [serverStatus, setServerStatus] = useState(null);

  useEffect(() => {
    checkServerHealth();
    const interval = setInterval(checkServerHealth, 30000); // Check every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const checkServerHealth = async () => {
    try {
      const status = await ApiService.checkHealth();
      setServerConnected(true);
      setServerStatus(status);
    } catch (error) {
      setServerConnected(false);
      setServerStatus(null);
    }
  };

  return (
    <AppContainer>
      <StatusIndicator connected={serverConnected}>
        {serverConnected ? '‚úì Connected' : '‚úó Offline'}
      </StatusIndicator>

      <Header>
        <Title>üõ°Ô∏è PassShield</Title>
        <Subtitle>Professional Password Security Analyzer</Subtitle>
      </Header>

      <ContentContainer>
        <PasswordAnalyzer />
      </ContentContainer>
    </AppContainer>
  );
}

export default App;
