import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import PasswordAnalyzer from './components/PasswordAnalyzer';
import ApiService from './services/api';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #1e3c72 0%, #2a5298 100%);
  padding: 0;
`;

const Header = styled.header`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  padding: 32px 0;
  text-align: center;
  color: white;
`;

const Title = styled.h1`
  font-size: 42px;
  font-weight: 800;
  margin: 0 0 12px 0;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
  background: linear-gradient(135deg, #ffffff 0%, #e0e7ff 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const Subtitle = styled.p`
  font-size: 18px;
  margin: 0;
  opacity: 0.9;
  font-weight: 500;
`;

const ContentContainer = styled.div`
  max-width: 1000px;
  margin: 0 auto;
  padding: 40px 20px;
`;



const StatusIndicator = styled.div`
  position: fixed;
  top: 24px;
  right: 24px;
  padding: 10px 18px;
  border-radius: 25px;
  font-size: 13px;
  font-weight: 600;
  backdrop-filter: blur(15px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  z-index: 1000;

  ${props => props.connected ? `
    background: rgba(34, 197, 94, 0.9);
    color: white;
  ` : `
    background: rgba(239, 68, 68, 0.9);
    color: white;
  `}
`;



function App() {
  const [serverConnected, setServerConnected] = useState(false);
  const [serverStatus, setServerStatus] = useState(null);

  useEffect(() => {
    checkServerHealth();
    const interval = setInterval(checkServerHealth, 30000); // Check every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const checkServerHealth = async () => {
    try {
      const status = await ApiService.checkHealth();
      setServerConnected(true);
      setServerStatus(status);
    } catch (error) {
      setServerConnected(false);
      setServerStatus(null);
    }
  };

  return (
    <AppContainer>
      <StatusIndicator connected={serverConnected}>
        {serverConnected ? '‚úì Connected' : '‚úó Offline'}
      </StatusIndicator>

      <Header>
        <Title>üõ°Ô∏è PassShield</Title>
        <Subtitle>Professional Password Security Analyzer</Subtitle>
      </Header>

      <ContentContainer>
        <PasswordAnalyzer />
      </ContentContainer>
    </AppContainer>
  );
}

export default App;
