import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import ApiService from '../services/api';
import WebSocketService from '../services/websocket';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const CrackerContainer = styled.div`
  background: white;
  border-radius: 12px;
  padding: 24px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  margin-bottom: 24px;
`;

const SectionTitle = styled.h2`
  color: #333;
  margin-bottom: 20px;
  font-size: 24px;
  font-weight: 600;
`;

const InputGroup = styled.div`
  margin-bottom: 16px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 8px;
  color: #333;
  font-weight: 500;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px 16px;
  border: 2px solid #e1e5e9;
  border-radius: 8px;
  font-size: 16px;
  font-family: ${props => props.mono ? "'Courier New', monospace" : 'inherit'};
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #667eea;
  }
`;

const Select = styled.select`
  width: 100%;
  padding: 12px 16px;
  border: 2px solid #e1e5e9;
  border-radius: 8px;
  font-size: 16px;
  background: white;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #667eea;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 12px;
  margin: 20px 0;
  flex-wrap: wrap;
`;

const Button = styled.button`
  padding: 12px 24px;
  border: none;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;

  ${props => props.primary && `
    background: #667eea;
    color: white;
    &:hover:not(:disabled) {
      background: #5a6fd8;
    }
  `}

  ${props => props.secondary && `
    background: #f8f9fa;
    color: #333;
    border: 2px solid #e1e5e9;
    &:hover:not(:disabled) {
      background: #e9ecef;
    }
  `}

  ${props => props.danger && `
    background: #ff4757;
    color: white;
    &:hover:not(:disabled) {
      background: #ff3838;
    }
  `}

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }
`;

const HashDisplay = styled.div`
  background: #f8f9fa;
  border: 2px solid #e1e5e9;
  border-radius: 8px;
  padding: 16px;
  margin: 16px 0;
`;

const HashTitle = styled.h4`
  margin: 0 0 12px 0;
  color: #333;
  font-size: 16px;
`;

const HashValue = styled.div`
  font-family: 'Courier New', monospace;
  word-break: break-all;
  color: #666;
  background: white;
  padding: 12px;
  border-radius: 6px;
  border: 1px solid #dee2e6;
`;

const StatusPanel = styled.div`
  background: ${props => {
    if (props.status === 'running') return '#e3f2fd';
    if (props.status === 'success') return '#e8f5e8';
    if (props.status === 'error') return '#ffebee';
    return '#f8f9fa';
  }};
  border: 2px solid ${props => {
    if (props.status === 'running') return '#2196f3';
    if (props.status === 'success') return '#4caf50';
    if (props.status === 'error') return '#f44336';
    return '#e1e5e9';
  }};
  border-radius: 8px;
  padding: 20px;
  margin: 20px 0;
`;

const StatusTitle = styled.h3`
  margin: 0 0 12px 0;
  color: #333;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const StatusGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 16px;
  margin-top: 16px;
`;

const StatCard = styled.div`
  text-align: center;
  padding: 12px;
`;

const StatValue = styled.div`
  font-size: 24px;
  font-weight: 700;
  color: #333;
  margin-bottom: 4px;
`;

const StatLabel = styled.div`
  font-size: 14px;
  color: #666;
`;

const ChartContainer = styled.div`
  height: 300px;
  margin: 20px 0;
`;

const ResultPanel = styled.div`
  background: #e8f5e8;
  border: 2px solid #4caf50;
  border-radius: 8px;
  padding: 20px;
  margin: 20px 0;
  text-align: center;
`;

const ResultTitle = styled.h3`
  color: #2e7d32;
  margin: 0 0 12px 0;
`;

const ResultPassword = styled.div`
  font-family: 'Courier New', monospace;
  font-size: 18px;
  font-weight: 700;
  color: #1b5e20;
  background: white;
  padding: 12px;
  border-radius: 6px;
  border: 1px solid #4caf50;
`;

const PasswordCracker = () => {
  const [testPassword, setTestPassword] = useState('');
  const [hashType, setHashType] = useState('md5');
  const [hashes, setHashes] = useState({});
  const [attackType, setAttackType] = useState('dictionary');
  const [targetHash, setTargetHash] = useState('');
  const [isAttacking, setIsAttacking] = useState(false);
  const [attackStatus, setAttackStatus] = useState(null);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [{
      label: 'Attempts per Second',
      data: [],
      borderColor: '#667eea',
      backgroundColor: 'rgba(102, 126, 234, 0.1)',
      tension: 0.4,
    }]
  });

  // Brute force settings
  const [charset, setCharset] = useState('abcdefghijklmnopqrstuvwxyz0123456789');
  const [minLength, setMinLength] = useState(1);
  const [maxLength, setMaxLength] = useState(4);

  useEffect(() => {
    // Setup WebSocket listeners
    WebSocketService.connect();

    WebSocketService.on('attack_progress', handleAttackProgress);
    WebSocketService.on('attack_complete', handleAttackComplete);
    WebSocketService.on('connection_status', handleConnectionStatus);

    return () => {
      WebSocketService.off('attack_progress', handleAttackProgress);
      WebSocketService.off('attack_complete', handleAttackComplete);
      WebSocketService.off('connection_status', handleConnectionStatus);
      WebSocketService.disconnect();
    };
  }, []);

  const handleConnectionStatus = (status) => {
    if (!status.connected) {
      setError('Lost connection to server');
    }
  };

  const handleAttackProgress = (progress) => {
    setAttackStatus(progress);

    // Update chart data
    if (progress.attempts_per_second) {
      setChartData(prevData => {
        const newLabels = [...prevData.labels, new Date().toLocaleTimeString()];
        const newData = [...prevData.datasets[0].data, progress.attempts_per_second];

        // Keep only last 20 data points
        if (newLabels.length > 20) {
          newLabels.shift();
          newData.shift();
        }

        return {
          ...prevData,
          labels: newLabels,
          datasets: [{
            ...prevData.datasets[0],
            data: newData,
          }]
        };
      });
    }
  };

  const handleAttackComplete = (result) => {
    setIsAttacking(false);
    setResult(result);
    if (result.found) {
      setError(null);
    }
  };

  const generateHashes = async () => {
    if (!testPassword.trim()) {
      setError('Please enter a password to hash');
      return;
    }

    setError(null);
    try {
      const response = await ApiService.hashPassword(testPassword);
      setHashes(response.hashes);
      setTargetHash(response.hashes[hashType] || '');
    } catch (err) {
      setError(err.message);
    }
  };

  const startAttack = async () => {
    if (!targetHash.trim()) {
      setError('Please generate a hash first or enter a target hash');
      return;
    }

    setError(null);
    setResult(null);
    setIsAttacking(true);
    setAttackStatus(null);
    setChartData({
      labels: [],
      datasets: [{
        label: 'Attempts per Second',
        data: [],
        borderColor: '#667eea',
        backgroundColor: 'rgba(102, 126, 234, 0.1)',
        tension: 0.4,
      }]
    });

    try {
      if (attackType === 'dictionary') {
        await ApiService.startDictionaryAttack(targetHash, hashType);
      } else if (attackType === 'brute_force') {
        await ApiService.startBruteForceAttack(
          targetHash,
          hashType,
          charset,
          minLength,
          maxLength
        );
      }
    } catch (err) {
      setError(err.message);
      setIsAttacking(false);
    }
  };

  const stopAttack = async () => {
    try {
      await ApiService.stopAttack();
      setIsAttacking(false);
    } catch (err) {
      setError(err.message);
    }
  };

  const formatTime = (seconds) => {
    if (seconds < 60) return `${seconds.toFixed(1)}s`;
    if (seconds < 3600) return `${(seconds / 60).toFixed(1)}m`;
    return `${(seconds / 3600).toFixed(1)}h`;
  };

  const formatNumber = (num) => {
    return new Intl.NumberFormat().format(Math.floor(num));
  };

  return (
    <CrackerContainer>
      <SectionTitle>‚ö° Password Cracking Demo</SectionTitle>

      {error && (
        <div style={{ 
          background: '#ffebee', 
          border: '2px solid #f44336', 
          borderRadius: '8px', 
          padding: '16px', 
          marginBottom: '20px',
          color: '#c62828'
        }}>
          ‚ö†Ô∏è {error}
        </div>
      )}

      <InputGroup>
        <Label>Test Password (to generate hash for cracking)</Label>
        <Input
          type="text"
          value={testPassword}
          onChange={(e) => setTestPassword(e.target.value)}
          placeholder="Enter a password to test..."
          mono
        />
      </InputGroup>

      <InputGroup>
        <Label>Hash Algorithm</Label>
        <Select value={hashType} onChange={(e) => setHashType(e.target.value)}>
          <option value="md5">MD5 (Fast, Insecure)</option>
          <option value="sha256">SHA-256 (Fast, Better)</option>
          <option value="bcrypt">bcrypt (Slow, Secure)</option>
          <option value="argon2">Argon2 (Slow, Very Secure)</option>
        </Select>
      </InputGroup>

      <ButtonGroup>
        <Button primary onClick={generateHashes} disabled={isAttacking}>
          üîê Generate Hashes
        </Button>
      </ButtonGroup>

      {Object.keys(hashes).length > 0 && (
        <div>
          <h3 style={{ color: '#333', marginBottom: '16px' }}>Generated Hashes</h3>
          {Object.entries(hashes).map(([type, hash]) => (
            <HashDisplay key={type}>
              <HashTitle>{type.toUpperCase()}</HashTitle>
              <HashValue>{hash.startsWith('Error:') ? hash : hash}</HashValue>
            </HashDisplay>
          ))}
        </div>
      )}

      <InputGroup>
        <Label>Target Hash (or use generated hash above)</Label>
        <Input
          type="text"
          value={targetHash}
          onChange={(e) => setTargetHash(e.target.value)}
          placeholder="Enter hash to crack..."
          mono
        />
      </InputGroup>

      <InputGroup>
        <Label>Attack Type</Label>
        <Select value={attackType} onChange={(e) => setAttackType(e.target.value)}>
          <option value="dictionary">Dictionary Attack</option>
          <option value="brute_force">Brute Force Attack</option>
        </Select>
      </InputGroup>

      {attackType === 'brute_force' && (
        <>
          <InputGroup>
            <Label>Character Set</Label>
            <Input
              type="text"
              value={charset}
              onChange={(e) => setCharset(e.target.value)}
              placeholder="Characters to use in brute force..."
              mono
            />
          </InputGroup>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px' }}>
            <InputGroup>
              <Label>Min Length</Label>
              <Input
                type="number"
                min="1"
                max="6"
                value={minLength}
                onChange={(e) => setMinLength(parseInt(e.target.value))}
              />
            </InputGroup>

            <InputGroup>
              <Label>Max Length (Limited to 6 for safety)</Label>
              <Input
                type="number"
                min="1"
                max="6"
                value={maxLength}
                onChange={(e) => setMaxLength(parseInt(e.target.value))}
              />
            </InputGroup>
          </div>
        </>
      )}

      <ButtonGroup>
        <Button 
          primary 
          onClick={startAttack} 
          disabled={isAttacking || !targetHash.trim()}
        >
          üöÄ Start {attackType === 'dictionary' ? 'Dictionary' : 'Brute Force'} Attack
        </Button>

        <Button danger onClick={stopAttack} disabled={!isAttacking}>
          üõë Stop Attack
        </Button>
      </ButtonGroup>

      {isAttacking && attackStatus && (
        <StatusPanel status="running">
          <StatusTitle>
            ‚ö° Attack in Progress
          </StatusTitle>
          <StatusGrid>
            <StatCard>
              <StatValue>{formatNumber(attackStatus.attempts || 0)}</StatValue>
              <StatLabel>Attempts</StatLabel>
            </StatCard>
            <StatCard>
              <StatValue>{formatNumber(attackStatus.attempts_per_second || 0)}</StatValue>
              <StatLabel>Attempts/sec</StatLabel>
            </StatCard>
            <StatCard>
              <StatValue>{formatTime(attackStatus.time_elapsed || 0)}</StatValue>
              <StatLabel>Time Elapsed</StatLabel>
            </StatCard>
            <StatCard>
              <StatValue>{attackStatus.current_password || 'N/A'}</StatValue>
              <StatLabel>Current Password</StatLabel>
            </StatCard>
          </StatusGrid>
        </StatusPanel>
      )}

      {chartData.labels.length > 0 && (
        <ChartContainer>
          <Line 
            data={chartData} 
            options={{
              responsive: true,
              maintainAspectRatio: false,
              plugins: {
                legend: {
                  position: 'top',
                },
                title: {
                  display: true,
                  text: 'Attack Speed Over Time'
                }
              },
              scales: {
                y: {
                  beginAtZero: true,
                  title: {
                    display: true,
                    text: 'Attempts per Second'
                  }
                }
              }
            }} 
          />
        </ChartContainer>
      )}

      {result && result.found && (
        <ResultPanel>
          <ResultTitle>üéâ Password Cracked!</ResultTitle>
          <ResultPassword>"{result.password}"</ResultPassword>
          <div style={{ marginTop: '12px', color: '#2e7d32' }}>
            Found in {formatNumber(result.final_status?.attempts || 0)} attempts 
            ({formatTime(result.final_status?.time_elapsed || 0)})
          </div>
        </ResultPanel>
      )}

      {result && !result.found && !isAttacking && (
        <StatusPanel status="error">
          <StatusTitle>‚ùå Attack Complete - Password Not Found</StatusTitle>
          <div>
            The password was not found using the {attackType} attack method.
            {result.final_status && (
              <div style={{ marginTop: '12px' }}>
                Attempted {formatNumber(result.final_status.attempts)} passwords 
                in {formatTime(result.final_status.time_elapsed)}.
              </div>
            )}
          </div>
        </StatusPanel>
      )}
    </CrackerContainer>
  );
};

export default PasswordCracker;
