import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import PasswordAnalyzer from './components/PasswordAnalyzer';
import ApiService from './services/api';

const AppContainer = styled.div`
  min-height: 100vh;
  background: #0f172a;
  position: relative;
  overflow-x: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
      linear-gradient(135deg, rgba(59, 130, 246, 0.1) 0%, transparent 50%),
      linear-gradient(225deg, rgba(236, 72, 153, 0.1) 0%, transparent 50%),
      radial-gradient(circle at 50% 0%, rgba(139, 92, 246, 0.15) 0%, transparent 50%);
    pointer-events: none;
  }

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
      radial-gradient(circle at 20% 20%, rgba(59, 130, 246, 0.05) 0%, transparent 25%),
      radial-gradient(circle at 80% 80%, rgba(236, 72, 153, 0.05) 0%, transparent 25%),
      radial-gradient(circle at 60% 40%, rgba(139, 92, 246, 0.05) 0%, transparent 25%);
    pointer-events: none;
  }
`;

const Header = styled.header`
  position: relative;
  z-index: 2;
  background: rgba(15, 23, 42, 0.8);
  backdrop-filter: blur(20px);
  border-bottom: 1px solid rgba(71, 85, 105, 0.3);
  padding: 60px 0;
  text-align: center;
`;

const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 20px;
  gap: 16px;
`;

const LogoIcon = styled.div`
  width: 64px;
  height: 64px;
  background: linear-gradient(135deg, #3b82f6 0%, #8b5cf6 50%, #ec4899 100%);
  border-radius: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 28px;
  box-shadow: 
    0 10px 30px rgba(59, 130, 246, 0.3),
    inset 0 1px 0 rgba(255, 255, 255, 0.2);
  position: relative;

  &::before {
    content: '';
    position: absolute;
    inset: 2px;
    background: linear-gradient(135deg, rgba(255,255,255,0.1) 0%, transparent 50%);
    border-radius: 14px;
  }
`;

const Title = styled.h1`
  font-size: 48px;
  font-weight: 800;
  margin: 0;
  background: linear-gradient(135deg, #f8fafc 0%, #cbd5e1 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  letter-spacing: -1px;
`;

const Subtitle = styled.p`
  font-size: 18px;
  margin: 12px 0 0 0;
  color: #94a3b8;
  font-weight: 500;
  letter-spacing: 0.5px;
`;

const ContentContainer = styled.div`
  position: relative;
  z-index: 2;
  max-width: 1000px;
  margin: 0 auto;
  padding: 80px 24px;
`;



const StatusIndicator = styled.div`
  position: fixed;
  top: 32px;
  right: 32px;
  padding: 12px 20px;
  border-radius: 50px;
  font-size: 14px;
  font-weight: 700;
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  z-index: 1000;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.12);
  transition: all 0.3s ease;

  ${props => props.connected ? `
    background: linear-gradient(135deg, rgba(34, 197, 94, 0.9) 0%, rgba(22, 163, 74, 0.9) 100%);
    color: white;
    box-shadow: 0 8px 32px rgba(34, 197, 94, 0.3);
  ` : `
    background: linear-gradient(135deg, rgba(239, 68, 68, 0.9) 0%, rgba(220, 38, 38, 0.9) 100%);
    color: white;
    box-shadow: 0 8px 32px rgba(239, 68, 68, 0.3);
  `}

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 12px 40px ${props => props.connected ? 'rgba(34, 197, 94, 0.4)' : 'rgba(239, 68, 68, 0.4)'};
  }
`;



function App() {
  const [serverConnected, setServerConnected] = useState(false);
  const [serverStatus, setServerStatus] = useState(null);

  useEffect(() => {
    checkServerHealth();
    const interval = setInterval(checkServerHealth, 30000); // Check every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const checkServerHealth = async () => {
    try {
      const status = await ApiService.checkHealth();
      setServerConnected(true);
      setServerStatus(status);
    } catch (error) {
      setServerConnected(false);
      setServerStatus(null);
    }
  };

  return (
    <AppContainer>
      <StatusIndicator connected={serverConnected}>
        {serverConnected ? '‚úì Connected' : '‚úó Offline'}
      </StatusIndicator>

      <Header>
        <Title>üõ°Ô∏è PassShield</Title>
        <Subtitle>Professional Password Security Analyzer</Subtitle>
      </Header>

      <ContentContainer>
        <PasswordAnalyzer />
      </ContentContainer>
    </AppContainer>
  );
}

export default App;
