const PasswordAnalyzer = () => {
  const [password, setPassword] = useState('');
  const [analysis, setAnalysis] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [error, setError] = useState(null);
  const [showPassword, setShowPassword] = useState(false);

  useEffect(() => {
    if (password.trim()) {
      analyzePassword();
    } else {
      setAnalysis(null);
    }
  }, [password]);

  const analyzePassword = async () => {
    if (!password.trim()) return;

    setIsAnalyzing(true);
    setError(null);

    try {
      const result = await ApiService.analyzePassword(password);
      setAnalysis(result);
    } catch (err) {
      setError(err.message);
      setAnalysis(null);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const formatAttackType = (type) => {
    return type.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  return (
    <AnalyzerContainer>
      <Title>ÔøΩ Analyze Password Security</Title>

      <PasswordInput
        type="password"
        placeholder="Type your password here to analyze its security..."
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      {error && (
        <div style={{ color: '#ff4757', marginBottom: '16px', padding: '12px', background: '#fff5f5', borderRadius: '6px' }}>
          ‚ö†Ô∏è {error}
        </div>
      )}

      {isAnalyzing && (
        <div style={{ color: '#666', marginBottom: '16px', textAlign: 'center' }}>
          üîç Analyzing password...
        </div>
      )}

      {analysis && (
        <>
          <StrengthMeter>
            <StrengthBar percentage={analysis.strength_score} />
          </StrengthMeter>

          <StrengthLabel>
            <StrengthText strength={analysis.strength_level}>
              {analysis.strength_level}
              {analysis.is_common && <WarningBadge>COMMON</WarningBadge>}
            </StrengthText>
            <ScoreText>{analysis.strength_score}/100</ScoreText>
          </StrengthLabel>

          <AnalysisGrid>
            <AnalysisCard color="#667eea">
              <CardTitle>Length</CardTitle>
              <CardValue>{analysis.length} characters</CardValue>
            </AnalysisCard>

            <AnalysisCard color="#26de81">
              <CardTitle>Entropy</CardTitle>
              <CardValue>{analysis.entropy.toFixed(1)} bits</CardValue>
            </AnalysisCard>

            <AnalysisCard color="#ffa502">
              <CardTitle>Character Set</CardTitle>
              <CardValue>{analysis.character_set_size} characters</CardValue>
            </AnalysisCard>

            <AnalysisCard color={analysis.is_common ? '#ff4757' : '#20bf6b'}>
              <CardTitle>Common Password</CardTitle>
              <CardValue color={analysis.is_common ? '#ff4757' : '#20bf6b'}>
                {analysis.is_common ? 'Yes' : 'No'}
              </CardValue>
            </AnalysisCard>
          </AnalysisGrid>

          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '12px', marginBottom: '20px' }}>
            <div style={{ textAlign: 'center', padding: '8px' }}>
              <div style={{ fontSize: '12px', color: '#666', marginBottom: '4px' }}>Lowercase</div>
              <div style={{ color: analysis.has_lowercase ? '#20bf6b' : '#ff4757' }}>
                {analysis.has_lowercase ? '‚úì' : '‚úó'}
              </div>
            </div>
            <div style={{ textAlign: 'center', padding: '8px' }}>
              <div style={{ fontSize: '12px', color: '#666', marginBottom: '4px' }}>Uppercase</div>
              <div style={{ color: analysis.has_uppercase ? '#20bf6b' : '#ff4757' }}>
                {analysis.has_uppercase ? '‚úì' : '‚úó'}
              </div>
            </div>
            <div style={{ textAlign: 'center', padding: '8px' }}>
              <div style={{ fontSize: '12px', color: '#666', marginBottom: '4px' }}>Numbers</div>
              <div style={{ color: analysis.has_digits ? '#20bf6b' : '#ff4757' }}>
                {analysis.has_digits ? '‚úì' : '‚úó'}
              </div>
            </div>
            <div style={{ textAlign: 'center', padding: '8px' }}>
              <div style={{ fontSize: '12px', color: '#666', marginBottom: '4px' }}>Special</div>
              <div style={{ color: analysis.has_special ? '#20bf6b' : '#ff4757' }}>
                {analysis.has_special ? '‚úì' : '‚úó'}
              </div>
            </div>
          </div>

          {analysis.crack_time_estimates && (
            <CrackTimeSection>
              <SectionTitle>‚è±Ô∏è Time to Crack Estimates</SectionTitle>
              <CrackTimeGrid>
                {Object.entries(analysis.crack_time_estimates).map(([attackType, timeEstimate]) => (
                  <CrackTimeItem key={attackType}>
                    <AttackType>{formatAttackType(attackType)}</AttackType>
                    <TimeEstimate>{timeEstimate}</TimeEstimate>
                  </CrackTimeItem>
                ))}
              </CrackTimeGrid>
            </CrackTimeSection>
          )}
        </>
      )}
    </AnalyzerContainer>
  );
};

