import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import PasswordAnalyzer from './components/PasswordAnalyzer';
import PasswordCracker from './components/PasswordCracker';
import ApiService from './services/api';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 20px;
`;

const Header = styled.header`
  text-align: center;
  margin-bottom: 40px;
  color: white;
`;

const Title = styled.h1`
  font-size: 48px;
  font-weight: 700;
  margin: 0 0 16px 0;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
`;

const Subtitle = styled.p`
  font-size: 20px;
  margin: 0 0 8px 0;
  opacity: 0.9;
`;

const Description = styled.p`
  font-size: 16px;
  margin: 0;
  opacity: 0.8;
  max-width: 800px;
  margin: 0 auto;
`;

const ContentContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const TabContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: 32px;
  gap: 8px;
`;

const Tab = styled.button`
  padding: 12px 24px;
  border: none;
  background: ${props => props.active ? 'white' : 'rgba(255, 255, 255, 0.2)'};
  color: ${props => props.active ? '#333' : 'white'};
  border-radius: 8px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  backdrop-filter: blur(10px);

  &:hover {
    background: ${props => props.active ? 'white' : 'rgba(255, 255, 255, 0.3)'};
  }
`;

const StatusIndicator = styled.div`
  position: fixed;
  top: 20px;
  right: 20px;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 14px;
  font-weight: 600;
  backdrop-filter: blur(10px);

  ${props => props.connected ? `
    background: rgba(76, 175, 80, 0.9);
    color: white;
  ` : `
    background: rgba(244, 67, 54, 0.9);
    color: white;
  `}
`;

const WarningBanner = styled.div`
  background: rgba(255, 193, 7, 0.9);
  color: #333;
  padding: 16px;
  border-radius: 8px;
  margin-bottom: 24px;
  text-align: center;
  font-weight: 600;
  backdrop-filter: blur(10px);
`;

const InfoSection = styled.div`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 12px;
  padding: 24px;
  margin-bottom: 24px;
  color: white;
`;

const InfoTitle = styled.h3`
  margin: 0 0 16px 0;
  font-size: 20px;
  font-weight: 600;
`;

const InfoGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  margin-top: 20px;
`;

const InfoCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  padding: 16px;
  border-radius: 8px;
`;

const CardTitle = styled.h4`
  margin: 0 0 8px 0;
  font-size: 16px;
  font-weight: 600;
`;

const CardText = styled.p`
  margin: 0;
  font-size: 14px;
  opacity: 0.9;
  line-height: 1.5;
`;

function App() {
  const [activeTab, setActiveTab] = useState('analyzer');
  const [serverConnected, setServerConnected] = useState(false);
  const [serverStatus, setServerStatus] = useState(null);

  useEffect(() => {
    checkServerHealth();
    const interval = setInterval(checkServerHealth, 30000); // Check every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const checkServerHealth = async () => {
    try {
      const status = await ApiService.checkHealth();
      setServerConnected(true);
      setServerStatus(status);
    } catch (error) {
      setServerConnected(false);
      setServerStatus(null);
    }
  };

  return (
    <AppContainer>
      <StatusIndicator connected={serverConnected}>
        {serverConnected ? '🟢 Server Connected' : '🔴 Server Offline'}
      </StatusIndicator>

      <Header>
        <Title>🛡️ PassShield</Title>
        <Subtitle>Password Cracking & Defense Toolkit</Subtitle>
        <Description>
          Learn about password security through interactive demonstrations. 
          Understand how attacks work and why strong passwords matter.
        </Description>
      </Header>

      <ContentContainer>
        <WarningBanner>
